function bbAlert(n,t){n=n.replace(/\n/g,"<br />");bootbox.alert({title:t,message:n})}function bbConfirm(n,t){return $(n).attr("confirmed")=="true"?!0:(bootbox.confirm(t,function(t){t&&$(n).attr("confirmed",t).trigger("click")}),!1)}function bbConfirmWithOption(n,t,i,r){return $(n).attr("confirmed")=="true"?!0:(bootbox.prompt({title:t,inputType:"checkbox",inputOptions:[{text:i,value:"true"}],callback:function(t){t!=null&&(t.length>0&&(r.value=t[0]),$(n).attr("confirmed",!0).trigger("click"))}}),!1)}function bbApproveDeny(n,t,i){return $(n).attr("approved")=="approved"||$(n).attr("approved")=="denied"?!0:(bootbox.dialog({message:"Debe aprobar el siguiente cambio.<br/><br/>"+t,title:"Se Requiere Aprobación",buttons:{success:{label:"Aprobado",className:"btn-success",callback:function(t){t!=null&&(i.value="approved",$(n).attr("approved","approved").trigger("click"))}},danger:{label:"Denegado",className:"btn-danger",callback:function(t){t!=null&&(i.value="denied",$(n).attr("approved","denied").trigger("click"))}},main:{label:"Cancelar",className:"btn-primary",callback:function(){return}}}}),!1)}function bbAllJustThisCancel(n,t,i){return $(n).attr("approved")=="all"||$(n).attr("approved")=="one"?!0:(bootbox.dialog({message:t,title:"Borrar Confirmación",buttons:{success:{label:"Todo",className:"btn-success",callback:function(t){t!=null&&(i.value="all",$(n).attr("approved","all").trigger("click"))}},danger:{label:"Solo Esto",className:"btn-success",callback:function(t){t!=null&&(i.value="one",$(n).attr("approved","one").trigger("click"))}},main:{label:"Cancelar",className:"btn-primary",callback:function(){return}}}}),!1)}